#include <stdio.h>
#include <stdbool.h>
#include <string.h>

bool validPalindrome(char *s) {
    int length = strlen(s);

    //printf("length: %d\n", length);

    bool palindrome = true;

    for (int i = 0; i < length / 2; i++) {
        if (s[i] != s[length - 1 - i]) {
            palindrome = false;
            break;
        }
    }

    if (palindrome) return true;

    for (int i = 0; i < length; i++) {
        palindrome = true;
        //printf("i: %d\n", i);

        for (int j = 0; j < length / 2; j++) {
            int x = 0;
            int y = 0;
            if (j >= i && i < length / 2) {
                x = 1;
            } else if (j >= length - 1 - i && i >= length / 2) {
                y = 1;
            }

            //printf("comparing %d with %d\n", j+x, length - 1 - j - y);
            if (s[j+x] != s[length - 1 - j - y]) {
                //printf("not equal\n");
                palindrome = false;
            }
        }

        if (palindrome) return true;
    }

    return false;
}

int main() {
    char string[] = "";

    printf("%s: %d", string, validPalindrome(string));

    return 0;
}
